    def _classify_content_type(self, context: str) -> str:
        """–ö–ª–∞—Å—Å–∏—Ñ–∏—Ü–∏—Ä—É–µ—Ç —Ç–∏–ø –∫–æ–Ω—Ç–µ–Ω—Ç–∞"""
        context_lower = context.lower()

        if any(word in context_lower for word in ['–æ—Å–Ω–æ–≤–∞–Ω', '—Å–æ–∑–¥–∞–Ω', '—É—á—Ä–µ–∂–¥–µ–Ω', '1993']):
            return "foundation"
        elif any(word in context_lower for word in ['–∫–∞–ø–∏—Ç–∞–ª', '–∞–∫—Ü–∏', '—Ä—É–±–ª']):
            return "financial"
        elif any(word in context_lower for word in ['—Ç—Ä—É–±–æ–ø—Ä–æ–≤–æ–¥', '–Ω–µ—Ñ—Ç–µ–ø—Ä–æ–≤–æ–¥', '–º–∞–≥–∏—Å—Ç—Ä–∞–ª—å']):
            return "infrastructure"
        elif any(word in context_lower for word in ['–ø—Ä–æ–µ–∫—Ç', '—Å—Ç—Ä–æ–∏—Ç–µ–ª—å—Å—Ç–≤–æ']):
            return "projects"
        elif any(word in context_lower for word in ['–¥–∏—Ä–µ–∫—Ç–æ—Ä', '—Å–æ–≤–µ—Ç', '—É–ø—Ä–∞–≤–ª–µ–Ω']):
            return "management"
        elif any(word in context_lower for word in ['1950', '1960', '1970', '1980', '1990']):
            return "history"
        elif any(word in context_lower for word in ['—Ç–µ—Ö–Ω–æ–ª–æ–≥–∏', '–ø–∞—Ç–µ–Ω—Ç', '–∏–Ω–Ω–æ–≤–∞—Ü']):
            return "technology"
        elif any(word in context_lower for word in ['—ç–∫–æ–ª–æ–≥–∏', '–æ—Ö—Ä–∞–Ω–∞ –æ–∫—Ä—É–∂–∞—é—â']):
            return "ecology"
        elif any(word in context_lower for word in ['—Ä–µ–≥–∏–æ–Ω', '–≥–µ–æ–≥—Ä–∞—Ñ–∏']):
            return "geography"
        else:
            return "general"

    def _classify_question_type(self, question: str) -> str:
        """–ö–ª–∞—Å—Å–∏—Ñ–∏—Ü–∏—Ä—É–µ—Ç —Ç–∏–ø –≤–æ–ø—Ä–æ—Å–∞"""
        question_lower = question.lower()

        if any(word in question_lower for word in ['–∫–æ–≥–¥–∞', '—Å–∫–æ–ª—å–∫–æ', '–≥–¥–µ', '–∫–∞–∫–æ–π –≥–æ–¥']):
            return "factual"
        elif any(word in question_lower for word in ['–ø–æ—á–µ–º—É', '–∫–∞–∫', '–∑–∞—á–µ–º']):
            return "analytical"
        elif any(word in question_lower for word in ['–∫–∞–∫–∏–µ']):
            return "enumerative"
        else:
            return "general"

    def _extract_time_period(self, context: str) -> str:
        """–ò–∑–≤–ª–µ–∫–∞–µ—Ç –≤—Ä–µ–º–µ–Ω–Ω–æ–π –ø–µ—Ä–∏–æ–¥ –∏–∑ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞"""
        years = re.findall(r'\b(19\d{2}|20\d{2})\b', context)
        if years:
            return f"{min(years)}-{max(years)}"
        return "unknown"

    def analyze_benchmark(self, triplets: List[Dict]):
        """–ê–Ω–∞–ª–∏–∑–∏—Ä—É–µ—Ç –±–µ–Ω—á–º–∞—Ä–∫"""
        print("üìä –ü–û–õ–ù–´–ô –ê–ù–ê–õ–ò–ó –ë–ï–ù–ß–ú–ê–†–ö–ê –ò–ó 40 –¢–†–ò–ü–õ–ï–¢–û–í")
        print("=" * 60)

        total_triplets = len(triplets)
        content_types = defaultdict(int)
        question_types = defaultdict(int)
        time_periods = defaultdict(int)

        for triplet in triplets:
            content_types[triplet['content_type']] += 1
            question_types[triplet['metadata']['question_type']] += 1
            time_periods[triplet['metadata']['time_period']] += 1

        # –û–±—â–∞—è —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞
        print(f"üìà –û–ë–©–ê–Ø –°–¢–ê–¢–ò–°–¢–ò–ö–ê:")
        print(f"   –í—Å–µ–≥–æ —Ç—Ä–∏–ø–ª–µ—Ç–æ–≤: {total_triplets}")
        avg_context = sum(t['metadata']['context_length'] for t in triplets) // total_triplets
        avg_answer = sum(t['metadata']['answer_length'] for t in triplets) // total_triplets
        print(f"   –°—Ä–µ–¥–Ω—è—è –¥–ª–∏–Ω–∞ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞: {avg_context} —Å–∏–º–≤–æ–ª–æ–≤")
        print(f"   –°—Ä–µ–¥–Ω—è—è –¥–ª–∏–Ω–∞ –æ—Ç–≤–µ—Ç–∞: {avg_answer} —Å–∏–º–≤–æ–ª–æ–≤")
        print(f"   –û–±—â–µ–µ –∫–∞—á–µ—Å—Ç–≤–æ: 1.0 (—ç–∫—Å–ø–µ—Ä—Ç–Ω–∞—è —Ä—É—á–Ω–∞—è –≥–µ–Ω–µ—Ä–∞—Ü–∏—è)")

        # –†–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –ø–æ —Ç–∏–ø–∞–º –∫–æ–Ω—Ç–µ–Ω—Ç–∞
        print(f"\nüè∑Ô∏è  –†–ê–°–ü–†–ï–î–ï–õ–ï–ù–ò–ï –ü–û –¢–ò–ü–ê–ú –ö–û–ù–¢–ï–ù–¢–ê:")
        for content_type, count in sorted(content_types.items(), key=lambda x: x[1], reverse=True):
            percentage = (count / total_triplets) * 100
            print(f"   - {content_type}: {count} —Ç—Ä–∏–ø–ª–µ—Ç–æ–≤ ({percentage:.1f}%)")

        # –†–∞—Å–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ –ø–æ —Ç–∏–ø–∞–º –≤–æ–ø—Ä–æ—Å–æ–≤
        print(f"\n‚ùì –†–ê–°–ü–†–ï–î–ï–õ–ï–ù–ò–ï –ü–û –¢–ò–ü–ê–ú –í–û–ü–†–û–°–û–í:")
        for question_type, count in sorted(question_types.items(), key=lambda x: x[1], reverse=True):
            percentage = (count / total_triplets) * 100
            print(f"   - {question_type}: {count} —Ç—Ä–∏–ø–ª–µ—Ç–æ–≤ ({percentage:.1f}%)")

        # –í—Ä–µ–º–µ–Ω–Ω—ã–µ –ø–µ—Ä–∏–æ–¥—ã
        print(f"\nüïê –û–•–í–ê–¢ –í–†–ï–ú–ï–ù–ù–´–• –ü–ï–†–ò–û–î–û–í:")
        historical_periods = {k: v for k, v in time_periods.items() if k != 'unknown'}
        for period, count in sorted(historical_periods.items()):
            print(f"   - {period}: {count} —Ç—Ä–∏–ø–ª–µ—Ç–æ–≤")

        # –û—Ü–µ–Ω–∫–∞ –ø–æ–ª–Ω–æ—Ç—ã
        coverage_score = self._calculate_coverage_score(content_types)
        print(f"\nüéØ –û–¶–ï–ù–ö–ê –ü–û–õ–ù–û–¢–´: {coverage_score}/10")

        if coverage_score >= 9:
            print("‚úÖ –ë–ï–ù–ß–ú–ê–†–ö –ò–î–ï–ê–õ–¨–ù–û –ü–û–î–ì–û–¢–û–í–õ–ï–ù –î–õ–Ø –•–ê–ö–ê–¢–û–ù–ê!")
        else:
            print("‚úÖ –ë–µ–Ω—á–º–∞—Ä–∫ –≥–æ—Ç–æ–≤ –∫ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—é")

    def _calculate_coverage_score(self, content_types: dict) -> int:
        """–†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ—Ç –æ—Ü–µ–Ω–∫—É –ø–æ–ª–Ω–æ—Ç—ã –æ—Ö–≤–∞—Ç–∞"""
        essential_categories = ['foundation', 'financial', 'infrastructure', 'projects',
                                'management', 'history', 'technology', 'ecology']
        covered_categories = [cat for cat in essential_categories if cat in content_types]

        return min(10, len(covered_categories) * 2)

    def save_benchmark(self, triplets: List[Dict], filename: str):
        """–°–æ—Ö—Ä–∞–Ω—è–µ—Ç –±–µ–Ω—á–º–∞—Ä–∫ –≤ —Ñ–∞–π–ª"""
        os.makedirs(os.path.dirname(filename), exist_ok=True)

        with open(filename, 'w', encoding='utf-8') as f:
            json.dump(triplets, f, ensure_ascii=False, indent=2)

        print(f"‚úÖ –ë–µ–Ω—á–º–∞—Ä–∫ —Å–æ—Ö—Ä–∞–Ω–µ–Ω: {filename}")

