                print(f"‚ùì –í–æ–ø—Ä–æ—Å: {triplet['question']}")
                print(f"‚úÖ –û—Ç–≤–µ—Ç: {triplet['answer']}")
                print(
                    f"üìä –ú–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ: {triplet['metadata']['question_type']} –≤–æ–ø—Ä–æ—Å, {triplet['metadata']['time_period']}")

                categories_shown.add(triplet['content_type'])
                examples_shown += 1


